    color: #fff;
}

#facebox .content {
    background: #171717;
}

tr.tr.g2 {
    background: #171717;
}

#wrapper_global {
    width: 984px;
    height: 100%;
    margin: 0 auto;
    background: #171717;
    min-height: 600px;
}

.liste_arrow li {

    color: #fff;

}

.g1 {
    background-color: #171717;
}

textarea {
    background: black;
    color: white;
}

input[type=text] {
    border: #ddd 1px solid;
    color: white;
    padding: 5px;
    width: 300px;
    background: black;
}

#header_img_right {position: relative;top: 20px; margin: 0 0 0 500px;}

#contentblock {
    background: #171717;
}

.footer_bg {
    background: #171717;
   background-image: none;
}
}

