    color: #ffdb4d;
}

.serp-adv__found {

    font-weight: 700;
    color: #fff;

}

.serp-title.serp-title_type_supertitle.serp-title_font-weight_bold.typo.typo_text_xl.typo_line_m.entity-search__title {
    color: #fff;
}

.cut2__visible {
    color: #888;
}

.serp-title.serp-title_type_title.typo.typo_text_l.typo_line_m.homonyms__title {
    color: #888;
}

.minibadge__text.minibadge__text_max-lines_3.minibadge__text_ellipsis_yes.typo.typo_text_m.typo_line_m {
    color: #888;
}

.object-footer_in-card_yes {

    margin: 25px -16px -18px;
    padding: 11px 16px 12px;
    text-align: left;
    background: #171717;
    border-top: 1px solid #333;
    border-bottom-right-radius: 8px;
    border-bottom-left-radius: 8px;

}

.serp-footer .serp-footer__search-settings .link {

    font-weight: 700;
    color: #fff;
    opacity: .45;

}

.serp-footer__main {

    position: relative;
    z-index: 1;
    overflow: hidden;
    min-width: auto;
    padding: 40px 116px;
    text-align: left;
    color: #fff;
    background-image: none;
    background-color: #131313;

}

.serp-footer__promo_gradient_red::after {

    background-image: none;

}

.entity-search__rating-line {

    color: #888;

}

.key-value__item {

    color: #888;

}

.button2.button2_size_m.button2_theme_pseudo.button2_pin_circle-circle.button2_type_link.button2_view_classic.entity-search__site.i-bem.button2_js_inited {

    color: #888;

}

.button2.button2_size_m.button2_theme_pseudo.button2_pin_circle-circle.button2_type_link.button2_view_classic.entity-search__site.i-bem {
    color: #888;
}

.rating-dynamic__hoverable .rating-dynamic_dislike:not([data-value="-1"])::before {

    content: '';
    position: absolute;
    left: -16px;
    right: -16px;
    top: -16px;
    bottom: -32px;
    z-index: 0;
    background: #171717;
        background-clip: border-box;
    background-clip: padding-box;
    box-shadow: 0 10px 20px -5px rgba(0,0,0,.4);
    border: 1px solid rgba(0,0,0,.059);
    border-radius: 8px;

}

.rating-dynamic__dislike_theme_black::after {
    color: #888;
}

.link.link_theme_outer.path__item.i-bem.link_js_inited {

    color: yellow;
    opacity: 0.5;

}

.link.link_theme_outer.path__item.i-bem {

    color: yellow;
    opacity: 0.5;

}

.link.link_theme_none.link_target_serp.pager__item.pager__item_kind_next.i-bem.link_js_inited {
    color: #fff;
}
}

