/* ==UserStyle==
@name           trakt tv dark
@namespace      github.com/openstyles/stylus
@version        1.0.0
@description    A new userstyle
@author         Me
==/UserStyle== */

@-moz-document url-prefix("https://trakt.tv/dashboard") {

#ondeck-wrapper {
    background-color: #171717;
    padding-bottom: 10px;
    color: #fff;
}

.subnav-wrapper.season-links {

    background: #131313;

}

#inbox-wrapper {
    background-color: #171717;
}

#huckster-desktop-wrapper.dark, #huckster-mobile-square-wrapper.dark {
    background-color: #171717;
}

#schedule-wrapper {
    background: #171717;
    color: #fff;
}

.titles-link {

    color: red !important;

}

#huckster-desktop-wrapper.light, #huckster-mobile-square-wrapper.light {
    background-color: #171717;
}

#hu-ck--st-er-desk-top-wrapper.light, #hu-ck--st-er-mob-ile-square-wrapper.light {
    background-color: #171717;
}    

#charts-wrapper {

    background: #171717;
    color: #fff;

}

#activity-wrapper {

    background: #171717;
    color: #fff;

}

#network-wrapper {

    background-color: #171717;
    color: #fff;

}

#network-wrapper .alert-no-data {

    background-color: #131313;

}

}

@-moz-document url-prefix("https://trakt.tv/shows/"), url-prefix("https://trakt.tv/movies/") {

#info-wrapper {

    background: #171717;

}

#overview {

    color: #fff;

}

.btn.btn-block.btn-summary.btn-watch {

    background: #131313;

}

.btn.btn-block.btn-summary.btn-collect {

    background: #171717;

}

.btn.btn-block.btn-summary.btn-list {

    background: #171717;

}

.btn.btn-block.btn-summary.btn-comment.new-comment-focus {

    background: #171717;

}

.tabs {

    background: #171717;

}

.summary-activity .tabs .tab {

    float: left;
    width: 25%;
    border: solid 1px #e1e1e1;
        border-bottom-color: rgb(225, 225, 225);
    color: #fff;
    font-family: proxima nova;
    line-height: 1;
    padding: 10px 10px 8px;
    transition: all .5s;
    text-decoration: none;
    background: #333 !important;

}

.users-wrapper {

    background-color: #171717;
    border: solid 1px #333;
    padding: 20px 20px 10px;
    margin-top: 20px;

}

.comment-wrapper .above-comment {

    padding: 20px 20px 10px;
    background-color: #171717;
    color: #fff;

}

.format-date {

    color: #fff;

}

.comment {

    background: #131313;

}

.comment-wrapper {

    background-color: #171717;
    border-radius: 2px;
    transition: all .5s;
    outline-color: transparent;

}

.under-comment {

    background: #171717;

}

.username.emojis-supported.emojis-converted {

    color: #fff !important;

}

#info-wrapper .sidebar .external li a {

    font-size: 13px;
    padding: 3px 5px;
    margin: 6px 0 0 6px;
    border: solid 1px #ddd;
    background-color: #333;
    color: #fff;
    font-family: proxima nova;
    border-radius: 3px;
    transition: all .5s;
    text-transform: none;
    display: inline-block;
    text-decoration: none;

}

.subnav-wrapper.season-links {
    background: #131313;
    border: solid 1px #333;
        border-top-color: rgb(51, 51, 51);
        border-top-style: solid;
        border-top-width: 1px;
    border-top: none;
}

#info-wrapper .season-links .links ul li a.selected {
    color: red;
    font-family: proxima nova bold;
}

/* .titles {
    background: #171717 !important;
    color: white;
}*/

#seasons-episodes-sortable .episode-stats {
    font-size: 13px;
    margin: 5px 0 -10px -20px;
    font-family: proxima nova;
    font-weight: 400;
    background-color: #171717;
    padding: 7px 0 7px 20px;
}

#seasons-episodes-sortable .under-info h3 a {
    color: red;
}

#seasons-episodes-sortable .under-info .titles {
    background-color: #171717;
}        
}

@-moz-document url-prefix("https://trakt.tv/movies/") {

#info-wrapper {
    background: #171717;
}
}

